const bodyParser = require('body-parser');
const ejs = require('ejs');
const app = express();
const mongoose = require('mongoose');



const Task = require('./models/Task');

app.use(bodyParser.urlencoded({extended: false}));
app.set("view engine", 'ejs');
app.use(express.static('public'));
app.use('/css', express.static(__dirname + 'public/css'))

let date = new Date();
let days = ['Lundi', 'Mardi', 'Mercredi', 'Jeudi', 'Vendredi', 'Samedi', 'Dimanche'];
let months = ['Janvier', 'Février', 'Mars', 'Avril', 'Mai', 'Juin', 'Juillet', 'Aout', 'Septembre', 'Octobre', 'Novembre', 'Decembre'];

connectToTheDatabase();

app.get('/', function(req, res) {
  Task.find({}, (err, tasks) => {
      res.render('index', {todoList:tasks, date: date, days:days, months: months});
  });
});

app.post('/', async (req, res) => {
  let newTask = new Task({
    name: req.body.nameTask
  });
  await newTask.save();
  res.redirect('/');
});

app.listen(3000, function() {
  console.log("Server started on port 3000");
});

function connectToTheDatabase() {
  mongoose.connect('mongodb://localhost:27017/Todolist', {useNewUrlParser: true, useUnifiedTopology: true});
}
